import React, { Fragment } from "react";
import { BrowserRouter as Route, Switch, useLocation } from "react-router-dom";
import Header from "./common/Header";
import CardList from "./home/CardList";
import User from "./user/User";

function App() {
  /*
    TODO: There is no need to add a <Router >, it is in index.js.

    The <CardList /> component should be shown when the user is on the index path.

    The <User /> component should be shown when the user is on the following path:
    /users/:userId

    Display <NotFound /> when appropriate
  */

function NotFound(){
  const location = useLocation();

  return (
    <h2>No match for <code>{location.pathname}</code></h2>
  )
}

  return (
    <Fragment>
      <Switch>
        <Route >
          <Header />
        </Route>
        <Route>
          <User />
        </Route>
        <Route>
          <CardList />
        </Route>
        <Route>
          <h2>Page not found!</h2>
        </Route>
      </Switch>
    </Fragment>
  );
}

export default App;
